import React from 'react';

import './index.css';

//Default page unless correct data submitted
function LoginPage() {
    const [name, setName] = React.useState("");
    const onNameChange = e => setName(e.target.value);

    const handleSubmit = e => {
        e.preventDefault();

        const requestOptions = {
            method: 'POST',
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({name})
        };

        fetch('/', requestOptions)
            .then(response => response.json())
            .then(res => console.log(res));
    };

    document.body.style.backgroundColor = "#fff";
    return (
        <div className="login-box">
            <h1>Account Login</h1>
            <div className="login-input">
                <form method='GET'>
                    <label>
                        First Name: <input type="text" id="lastName" value={name} onChange={onNameChange} required/>
                    </label>
                    <br /><br />
                    <label>
                        Last Name: <input type="text" id="firstName" required/>
                    </label>
                    <br /><br />
                    <label>
                        Password: <input type="password" maxLength="8" id="password" required/>
                    </label>
                    <br /><br />
                    <input type="submit" value="Submit" onClick={() => {handleSubmit();console.log({name});}}/>
                </form>
            </div>
        </div>
    );

}

export default LoginPage;
