import React from 'react';
import './index.css';

//Handles notes data and keeps track of history
function SubmitNote() { 
    let note = document.getElementById("note").value;
    let history = document.getElementById("theNote").innerText;
    let format = history + "<br><br>";
    document.getElementById("theNote").innerHTML = format + note;
    document.getElementById("note").value = "";
    return ;
}

//Section 4
class Notes extends React.Component {
    constructor(){
        super();
        this.state={
            record: [""],
        }
    }

/*     addNote = note => {
        this.setState({
            record: [
                ...this.state.record,
                ...note
            ]
        })
    } */

    //this.setState({scribble: this.state.scribble.concat('new note')});

    render() {
        return (
            <div className='notes'>
                <div className='sub-header'>
                    <h3>Notes</h3>
                </div>

                <div className='note-input'>
                    <input type="text" size="50" placeholder="write a note" id="note" ref={this.notePad}></input>
                    <button onClick={() => {SubmitNote()}}>Submit</button>
                </div>
                <div>
                    <h4 id="noteHeader">Note History</h4>
                    <p id="theNote"></p>
                </div>
            </div>

        );
    }
}

export default Notes;
