import React from 'react';

import './App.css';


import TopHeader from './modules/TopHeader';
import Schedule from './modules/Schedule';
import News from './modules/News';
import Notes from './modules/Notes';
import Phone from './modules/Phone';
import LoginPage from './modules/LoginPage';
/////////////////////////////////////////////


/* 
                <TopHeader />
                <div id='row1'>
                    <div className='column'><Schedule /></div>
                    <div className='column'><Phone /></div>
                </div>

                <div id='row2'>
                    <div className='column'><News /></div>
                    <div className='column'><Notes /></div>
                </div>

                <LoginPage />
*/

//Main App layout
class App extends React.Component {
    state = {
        auth: "",
    }

    handleCallback = (loginData) => {
        this.setState({auth: loginData})
    }

    render() {
        const {auth} = this.state;
        console.log(this.state.auth);
        console.log(auth + " From App Component");
        const validator = auth;

        return (
            <div>
                <div>
                    <h1>{auth}</h1>
                    {!validator ?  <LoginPage loginCallback = {this.handleCallback}/> : <div><TopHeader /> 
                                        <div id='row1'>
                                        <div className='column'><Schedule /></div>
                                        <div className='column'><Phone /></div>
                                    </div>

                                    <div id='row2'>
                                        <div className='column'><News /></div>
                                        <div className='column'><Notes /></div>
                                    </div>
                                    </div>
                    }
                </div>

            </div>
        )
    }
}

export default App;
