import React from 'react';

import './index.css';

//Default page unless correct data submitted
class LoginPage extends React.Component {

    state = {
        data: null
    };

    componentDidMount() {
        this.callBackendAPI()
            .then(res => this.setState({ data: res.express }))
            .catch(err => console.log(err));
    }

    callBackendAPI = async () => {
        const response = await fetch('/got');
        const body = await response.json();

        if (response.status !== 200) {
            throw Error(body.message)
        }
        return body;
    };

    render() {
    document.body.style.backgroundColor = "#fff";
        function handleClick(){
            console.log("Working");

            fetch('/auth', {
                method: "POST",
                mode: "cors",
                headers: {"Content-Type": "application/json; charset=UTF-8" },
                body: JSON.stringify({message: "password"})
            })
            .then(res => {console.log(res.value);});

        };

        return (
            <div className="login-box">
                <h1>Account Login</h1>
                <div className="login-input">
                    <form>
                        <label>
                            First Name: <input type="text" id="lastName" required/>
                        </label>
                        <br /><br />
                        <label>
                            Last Name: <input type="text" id="firstName" required/>
                        </label>
                        <br /><br />
                        <label>
                            Password: <input type="password" maxLength="8" id="password" required/>
                        </label>
                        <br /><br />
                        <input type="submit" value="Submit" onClick={() => {handleClick()}} required/>
                    </form>
                    <p>{this.state.data}</p>
                </div>
            </div>
        );
    }
}

export default LoginPage;
