import React from 'react';

import './index.css';

//Default page unless correct data submitted
function LoginPage() {
    const [data, setData] = React.useState(null);

/*     React.useEffect(() => {
        fetch("/")
            .then((res) => res.json())
            .then((data) => setData(data.name));
    }, []); */

/*     React.useEffect(() => {
        fetch("/", {
            method: 'POST',
            headers: {"Content-Type": "application/JSON"},
            body: JSON.stringify({
                message: "Body Here"
            }),
        })

            .then((res) => {
                return res.json();
            })
            .then((data) => console.log(data));
    }, []); */

    const[text,setText] = React.useState('');
    const handleSubmit = (e) => {
        e.preventDefault();
        const newText = {text};
        fetch("/", {
            method: "POST",
            headers: {"Content-Type":"application/JSON"},
            body: JSON.stringify({message: "Hello"})
        })
    }

    document.body.style.backgroundColor = "#fff";
    return (
        <div className="login-box">
            <h1>Account Login</h1>
            <div className="login-input">
                <form onSubmit={handleSubmit}>
                    <label>
                        First Name: <input type="text" id="lastName" required/>
                    </label>
                    <br /><br />
                    <label>
                        Last Name: <input type="text" id="firstName" required/>
                    </label>
                    <br /><br />
                    <label>
                        Password: <input type="password" maxLength="8" id="password" required/>
                    </label>
                    <br /><br />
                    <input type="submit" value="Submit"/>
                </form>
                <p>{!data ? "Loading..." : data}</p>
            </div>
        </div>
    );

}

export default LoginPage;
