import React from 'react';
import App from './';
import './index.css';

//Default page unless correct data submitted
class LoginPage extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            data: "", 
            auth: "", //used to toggle displays depending on state
            firstName: "",
            lastName: "",
            password:""
        };
        this.onSubmission = this.onSubmission.bind(this);
        this.FN = React.createRef();
        this.LN = React.createRef();
        this.PW = React.createRef();

    }

    onSubmission = (event) => {
        //console.log(this.FN.current.value); //first name input reference
        //console.log(this.LN.current.value); //last name input reference
        //console.log(this.PW.current.value); //password input reference

        //this.handleClick();

        return 0;
    }

    serverCheck() {
        this.callBackendAPI()
            .then(res => this.setState({ data: res.express }))
            .catch(err => console.log(err));
    }

    callBackendAPI = async () => {
        const response = await fetch('/check');
        const body = await response.json();

        if (response.status !== 200) {
            throw Error(body.message)
        }
        return body;
    };

    handleClick() {

        fetch('/api', {
            method: "POST",
            mode: "cors",
            headers: {"Content-Type": "application/json; charset=UTF-8" },
            //body: JSON.stringify({message: this.state.password, fName: this.state.firstName, lName: this.state.lastName})
            body: JSON.stringify({message: this.PW.current.value, fName: this.FN.current.value, lName: this.LN.current.value})
        })
        .then(res => res.json())
        .then((res) => {
            console.log(res.authenticated); 
            this.setState({auth: res.authenticated});
            if (this.state.auth ){
                console.log("Signed in as: " + this.FN.current.value);
            }
            return res.authenticated;
        })
    };

    render() {

    document.body.style.backgroundColor = "#fff";

        return (
            <div>
                <div className="login-box">
                    <h1>Account Login</h1>
                <div className="login-input">

                        <label>
                            First Name: <input type="text" ref={this.FN} required/>
                        </label>
                        <br /><br />

                        <label>
                            Last Name: <input type="text" ref={this.LN} required/>
                        </label>
                        <br /><br />
                        <label>
                            Password: <input type="password" maxLength="8" ref={this.PW} required/>
                        </label>
                        <br /><br />

                        <input type="submit" value="Submit" onClick={() => {this.handleClick();}} required/>


                    <button onClick = {() => {this.serverCheck();}}> 
                        Check Server
                    </button>
                    <p>{this.state.data} </p>
                </div>

            </div>

            </div>

        );
    }
}


export default LoginPage;
