import React from 'react';

import './index.css';

//Default page unless correct data submitted
class LoginPage extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            data: "",
            auth: "",
            firstName: "",
            lastName: "world",
            password:"password"
        };
        this.onSubmission = this.onSubmission.bind(this);
        this.FN = React.createRef();
        this.LN = React.createRef();
        this.PW = React.createRef();
    }

    onSubmission = (event) => {
        console.log(this.FN.current.value);
        console.log(this.LN.current.value);
        console.log(this.PW.current.value);

        this.handleClick();

        return 0;
    }


    serverCheck() {
        this.callBackendAPI()
            .then(res => this.setState({ data: res.express }))
            .catch(err => console.log(err));
    }

    callBackendAPI = async () => {
        const response = await fetch('/check');
        const body = await response.json();

        if (response.status !== 200) {
            throw Error(body.message)
        }
        return body;
    };


    handleClick() {

        fetch('/api', {
            method: "POST",
            mode: "cors",
            headers: {"Content-Type": "application/json; charset=UTF-8" },
            body: JSON.stringify({message: this.state.password, fName: this.state.firstName, lName: this.state.lastName})
        })
        .then(res => res.json())
        .then((res) => {
            console.log(res.authenticated); 
            this.setState({auth: res.authenticated});
            console.log("XXXXX" + this.FN.current.value)
            return res.authenticated;
        })
    };

//onClick={() => {this.handleClick();}}


/*                         <label>
                            Last Name: <input type="text"  required/>
                        </label>
                        <br /><br />
                        <label>
                            Password: <input type="password" maxLength="8"  required/>
                        </label>
                        <br /><br />
 */


                        //onSubmit={this.onSubmission}
                        //onClick={() => {this.handleClick();}}

    render() {

    document.body.style.backgroundColor = "#fff";

        return (
            <div className="login-box">
                <h1>Account Login</h1>
                <div className="login-input">
                    <form onSubmit={this.handleClick()}>
                        <label>
                            First Name: <input type="text" defaultValue={"A"} ref={this.FN} required/>
                        </label>
                        <br /><br />

                        <label>
                            Last Name: <input type="text" defaultValue={"B"} ref={this.LN} required/>
                        </label>
                        <br /><br />
                        <label>
                            Password: <input type="password" maxLength="8" defaultValue={"C"} ref={this.PW} required/>
                        </label>
                        <br /><br />




                        <input type="submit" value="Submit"  required/>
                    </form>

                    <button onClick = {() => {this.serverCheck();}}> 
                        Check Server
                    </button>
                    <p>{this.state.data} </p>
                </div>
            </div>
        );
    }
}

export default LoginPage;
