import React from 'react';

import './index.css';

//Default page unless correct data submitted
class LoginPage extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            data: "",
            auth: "",
            firstName: "",
            lastName: "",
            password:"password"
        };
    }


/*      function testing(){
            setTimeout(() => {
                console.log("hello world");
            }, 1000)
            return 0;
        } */

    //called after the component is rendered
    //componentDidMount() {
    //    this.callBackendAPI()
    //        .then(res => this.setState({ data: res.express }))
    //       .catch(err => console.log(err));
    //}

    serverCheck() {
        this.callBackendAPI()
            .then(res => this.setState({ data: res.express }))
            .catch(err => console.log(err));
    }

    callBackendAPI = async () => {
        const response = await fetch('/got');
        const body = await response.json();

        if (response.status !== 200) {
            throw Error(body.message)
        }
        return body;
    };


    handleClick(){
        fetch('/api', {
            method: "POST",
            mode: "cors",
            headers: {"Content-Type": "application/json; charset=UTF-8" },
            body: JSON.stringify({message: this.state.password})//"password"})
        })
        .then(res => res.json())
        .then((res) => {
            console.log(res.authenticated); 
            this.setState({auth: res.authenticated});
            console.log(this.state.password);
            return res.authenticated;
        })
    };


    render() {

    document.body.style.backgroundColor = "#fff";

        return (
            <div className="login-box">
                <h1>Account Login</h1>
                <div className="login-input">
                    <form>
                        <label>
                            First Name: <input type="text" id="lastName" required/>
                        </label>
                        <br /><br />
                        <label>
                            Last Name: <input type="text" id="firstName" required/>
                        </label>
                        <br /><br />
                        <label>
                            Password: <input type="password" maxLength="8" id="password" required/>
                        </label>
                        <br /><br />
                        <input type="submit" value="Submit" onClick={() => {this.handleClick();}} required/>
                    </form>
                    <p>{this.state.auth}</p>


                    <button onClick = {() => {this.serverCheck();}}> Check Server </button>
                    <p>{this.state.data}</p>

                </div>
            </div>
        );
    }
}

export default LoginPage;
