import React from 'react';

import './index.css';

//Default page unless correct data submitted
function LoginPage() {
    //const [data, setData] = React.useState();

    var userData = {
        "firstName": "",
        "lastName": "",
        "password": ""
    }

    function handleClick() {
        var formData = new FormData();
        formData.append(JSON.stringify(userData));

        fetch('/', {
            method: 'POST',
            mode: 'cors',
            body: formData
        })
    }

    document.body.style.backgroundColor = "#fff";
    return (
        <div className="login-box">
            <h1>Account Login</h1>
            <div className="login-input">
                <form onSubmit={() => {handleClick}}>
                    <label>
                        First Name: <input type="text" id="lastName" required/>
                    </label>
                    <br /><br />
                    <label>
                        Last Name: <input type="text" id="firstName" required/>
                    </label>
                    <br /><br />
                    <label>
                        Password: <input type="password" maxLength="8" id="password" required/>
                    </label>
                    <br /><br />
                    <input type="submit" value="Submit"/>
                </form>
                <button onClick={() => {console.log({userData})}}>H</button>
            </div>
        </div>
    );

}

export default LoginPage;
