    var userData = {
        "firstName": "",
        "lastName": "",
        "password": ""
    };

    function handleClick() {
        console.log("Function triggered");
    }

    console.log(userData);

    document.body.style.backgroundColor = "#fff";
    return (
        <div className="login-box">
            <h1>Account Login</h1>
            <div className="login-input">
                <form onClick={handleClick}>
                    <label>
                        First Name: <input type="text" id="lastName" required/>
                    </label>
                    <br /><br />
                    <label>
                        Last Name: <input type="text" id="firstName" required/>
                    </label>
                    <br /><br />
                    <label>
                        Password: <input type="password" maxLength="8" id="password" required/>
                    </label>
                    <br /><br />
                    <input type="submit" value="Submit"/>
                </form>
            </div>
        </div>
    );

}

export default LoginPage;
