import React from 'react';
import './index.css';

//Handles notes data and keeps track of history
/* function SubmitNote() { 
    let note = document.getElementById("note").value;
    let history = document.getElementById("theNote").innerText;
    let format = history + "<br><br>";
    document.getElementById("theNote").innerHTML = format + note;
    document.getElementById("note").value = "";
    return ;
} */

function clearInput(){
    document.getElementById("note").value = "";
};

//Section 4
class Notes extends React.Component {
    constructor(props){
        super(props);
        this.state={
            value: "",
            record: [],
        };
        this.notePad = React.createRef();
    }

    onUpdateNote = () => {
        this.setState(state => {
            const record = state.record.concat(this.notePad.current.value, " " );
            const spacer = state.record.concat(" ");
            return {
                record,
                value: this.notePad.current.value,
            }
        });

    };

    render() {

        return (
            <div className='notes'>
                <div className='sub-header'>
                    <h3>Notes</h3>
                </div>

                <div className='note-input'>
                    <input type="text" size="50" placeholder="write a note" id="note" defaultValue="" ref={this.notePad}></input>
                    <button onClick={() => {this.onUpdateNote();clearInput();}}>Submit</button><button onClick={() => {clearInput();}}>Reset</button>
                </div>
                <div>
                    <h4 id="noteHeader">Note History</h4>

                    <ul id="theNote">
                        {this.state.record.map((record, id) => (
                            <li key={record.in}>{record}</li>
                        ))}
                    </ul>
                </div>
            </div>

        );
    }
}

export default Notes;
