import React from 'react';

import './index.css';

//Default page unless correct data submitted
function LoginPage() {
    const [data, setData] = React.useState(null);

/*     React.useEffect(() => {
        fetch("/")
            .then((res) => res.json())
            .then((data) => setData(data.name));
    }, []); */

    React.useEffect(() => {
        fetch("/", {
            method: 'POST',
            headers: 'Content-Type',
            body: JSON.stringify({
                username,
                password,
            }),
        })

            .then((res) => {
                return res.json();
            })
            .then((data) => console.log(data));    

    }, []);

    document.body.style.backgroundColor = "#fff";
    return (
        <div className="login-box">
            <h1>Account Login</h1>
            <div className="login-input">
                <form action="/" method="POST">
                    <label>
                        First Name: <input type="text" id="lastName" />
                    </label>
                    <br /><br />
                    <label>
                        Last Name: <input type="text" id="firstName"/>
                    </label>
                    <br /><br />
                    <label>
                        Password: <input type="password" maxLength="8" id="password"/>
                    </label>
                    <br /><br />
                    <input type="submit" />
                </form>
                <p>{!data ? "Loading..." : data}</p>
            </div>
        </div>
    );

}

export default LoginPage;
